// Propriedade Rural Detalhada
export interface PropertyDetail {
  id: string;
  ownerId: string;                // ID do produtor dono
  name: string;                   // Nome da propriedade
  description?: string;

  // Localizacao
  address: string;                // Endereco completo
  city?: string;
  state?: string;
  cep?: string;
  latitude: number;
  longitude: number;

  // Geometria
