import React, { useState } from 'react';
import { StyleSheet, View, TextInput, Pressable, Alert, ActivityIndicator, ScrollView, FlatList, Image as RNImage } from 'react-native';
import { useNavigation } from '@react-navigation/native';
import { NativeStackNavigationProp } from '@react-navigation/native-stack';
import { Feather } from '@expo/vector-icons';
import { useSafeAreaInsets } from 'react-native-safe-area-context';
import * as Location from 'expo-location';
import * as ImagePicker from 'expo-image-picker';
import { ScreenKeyboardAwareScrollView } from '@/components/ScreenKeyboardAwareScrollView';
import { ThemedText } from '@/components/ThemedText';
import { ThemedView } from '@/components/ThemedView';
import { Button } from '@/components/Button';
import { useTheme } from '@/hooks/useTheme';
import { useAuth } from '@/hooks/useAuth';
import { Colors, Spacing, BorderRadius, Shadows } from '@/constants/theme';
import { RootStackParamList } from '@/navigation/RootNavigator';
import { SERVICE_TYPES } from '@/data/serviceTypes';
import { createJob } from '@/utils/storage';
import { formatCurrency } from '@/utils/format';

export default function CreateJobScreen() {
  const navigation = useNavigation<NativeStackNavigationProp<RootStackParamList>>();
  const { theme, isDark } = useTheme();
  const { user } = useAuth();
  const insets = useSafeAreaInsets();
  const colors = isDark ? Colors.dark : Colors.light;

  const [serviceTypeId, setServiceTypeId] = useState('');
  const [quantity, setQuantity] = useState('');
  const [locationText, setLocationText] = useState('');
  const [latitude, setLatitude] = useState<number | undefined>();
  const [longitude, setLongitude] = useState<number | undefined>();
  const [offer, setOffer] = useState('');
  const [notes, setNotes] = useState('');
  const [photos, setPhotos] = useState<string[]>([]);
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [isGettingLocation, setIsGettingLocation] = useState(false);
  const [isPickingPhoto, setIsPickingPhoto] = useState(false);

  const selectedService = SERVICE_TYPES.find((s) => s.id === serviceTypeId);
  const suggestedPrice = selectedService ? selectedService.basePrice * (parseFloat(quantity) || 0) : 0;

  const handleGetLocation = async () => {
    setIsGettingLocation(true);
    try {
      const { status } = await Location.requestForegroundPermissionsAsync();
      if (status !== 'granted') {
        Alert.alert('Permissão negada', 'Precisamos de acesso à localização');
        return;
      }
      const location = await Location.getCurrentPositionAsync({});
      setLatitude(location.coords.latitude);
      setLongitude(location.coords.longitude);
      const [address] = await Location.reverseGeocodeAsync({
        latitude: location.coords.latitude,
        longitude: location.coords.longitude,
      });
      if (address) {
        const parts = [address.street, address.district, address.city].filter(Boolean);
        setLocationText(parts.join(', ') || 'Localização obtida');
      }
    } catch (error) {
      console.error('Error getting location:', error);
      Alert.alert('Erro', 'Não foi possível obter a localização');
    } finally {
      setIsGettingLocation(false);
    }
  };

  const handlePickPhoto = async () => {
    setIsPickingPhoto(true);
    try {
      const result = await ImagePicker.launchImageLibraryAsync({
        mediaTypes: ImagePicker.MediaTypeOptions.Images,
        allowsEditing: true,
        aspect: [4, 3],
        quality: 0.7,
      });

      if (!result.canceled) {
        setPhotos([...photos, result.assets[0].uri]);
      }
    } catch (error) {
      Alert.alert('Erro', 'Não foi possível selecionar a foto');
    } finally {
      setIsPickingPhoto(false);
    }
  };

  const handleRemovePhoto = (index: number) => {
    setPhotos(photos.filter((_, i) => i !== index));
  };

  const handleSubmit = async () => {
    if (!serviceTypeId) {
      Alert.alert('Erro', 'Selecione um tipo de serviço');
      return;
    }
    if (!quantity || parseFloat(quantity) <= 0) {
      Alert.alert('Erro', 'Informe a quantidade');
      return;
    }
    if (!locationText.trim()) {
      Alert.alert('Erro', 'Informe a localização');
      return;
    }
    if (!offer || parseFloat(offer) <= 0) {
      Alert.alert('Erro', 'Informe o valor da oferta');
      return;
    }
    if (!notes.trim()) {
      Alert.alert('Atenção', 'Por favor, adicione uma descrição detalhada da demanda');
      return;
    }

    setIsSubmitting(true);
    try {
      await createJob({
        producerId: user!.id,
        serviceTypeId,
        quantity: parseFloat(quantity),
        locationText: locationText.trim(),
        latitude,
        longitude,
        offer: parseFloat(offer),
        notes: notes.trim(),
        photos: photos.length > 0 ? photos : undefined,
      });
      Alert.alert('Sucesso', 'Demanda criada com sucesso!', [
        { text: 'OK', onPress: () => navigation.goBack() },
      ]);
    } catch (error: any) {
      Alert.alert('Erro', error.message || 'Não foi possível criar a demanda');
    } finally {
      setIsSubmitting(false);
    }
  };

  return (
    <ThemedView style={styles.container}>
      <ScreenKeyboardAwareScrollView
        contentContainerStyle={[
          styles.scrollContent,
          { paddingTop: Spacing.xl, paddingBottom: insets.bottom + Spacing['2xl'] },
        ]}
      >
        <ThemedText type="small" style={styles.sectionTitle}>
          Tipo de Serviço
        </ThemedText>
        <ScrollView
          horizontal
          showsHorizontalScrollIndicator={false}
          contentContainerStyle={styles.servicesContainer}
        >
          {SERVICE_TYPES.map((service) => (
            <Pressable
              key={service.id}
              style={[
                styles.serviceChip,
                {
                  backgroundColor:
                    serviceTypeId === service.id ? colors.primary : colors.backgroundDefault,
                  borderColor: colors.border,
                },
              ]}
              onPress={() => setServiceTypeId(service.id)}
            >
              <Feather
                name={service.icon as any}
                size={18}
                color={serviceTypeId === service.id ? '#FFFFFF' : colors.text}
              />
              <ThemedText
                type="small"
                style={{ color: serviceTypeId === service.id ? '#FFFFFF' : colors.text }}
              >
                {service.name}
              </ThemedText>
            </Pressable>
          ))}
        </ScrollView>

        {selectedService && (
          <View style={[styles.infoBox, { backgroundColor: colors.primary + '10' }]}>
            <Feather name="info" size={16} color={colors.primary} />
            <ThemedText type="small" style={{ color: colors.primary, flex: 1 }}>
              Nível mínimo: N{selectedService.minLevel} | Preço base: {formatCurrency(selectedService.basePrice)}/{selectedService.unit}
            </ThemedText>
          </View>
        )}

        <View style={styles.inputContainer}>
          <ThemedText type="small" style={styles.label}>
            Quantidade {selectedService ? `(${selectedService.unit})` : ''}
          </ThemedText>
          <View
            style={[
              styles.inputWrapper,
              { backgroundColor: colors.backgroundDefault, borderColor: colors.border },
            ]}
          >
            <TextInput
              style={[styles.input, { color: colors.text }]}
              placeholder="Ex: 100"
              placeholderTextColor={colors.textSecondary}
              value={quantity}
              onChangeText={setQuantity}
              keyboardType="numeric"
            />
          </View>
          {suggestedPrice > 0 && (
            <ThemedText type="caption" style={{ color: colors.textSecondary, marginTop: Spacing.xs }}>
              Sugestão: {formatCurrency(suggestedPrice)} (base)
            </ThemedText>
          )}
        </View>

        <View style={styles.inputContainer}>
          <ThemedText type="small" style={styles.label}>
            Localização
          </ThemedText>
          <View
            style={[
              styles.inputWrapper,
              { backgroundColor: colors.backgroundDefault, borderColor: colors.border },
            ]}
          >
            <Feather name="map-pin" size={20} color={colors.textSecondary} />
            <TextInput
              style={[styles.input, { color: colors.text }]}
              placeholder="Endereço ou descrição"
              placeholderTextColor={colors.textSecondary}
              value={locationText}
              onChangeText={setLocationText}
            />
          </View>
          <Pressable
            style={[styles.locationButton, { backgroundColor: colors.primary }]}
            onPress={handleGetLocation}
            disabled={isGettingLocation}
          >
            {isGettingLocation ? (
              <ActivityIndicator size="small" color="#FFFFFF" />
            ) : (
              <>
                <Feather name="navigation" size={16} color="#FFFFFF" />
                <ThemedText type="small" style={{ color: '#FFFFFF' }}>
                  Usar minha localização
                </ThemedText>
              </>
            )}
          </Pressable>
          {latitude && longitude && (
            <ThemedText type="caption" style={{ color: colors.success, marginTop: Spacing.xs }}>
              GPS capturado com sucesso
            </ThemedText>
          )}
        </View>

        <View style={styles.inputContainer}>
          <ThemedText type="small" style={styles.label}>
            Oferta (R$)
          </ThemedText>
          <View
            style={[
              styles.inputWrapper,
              { backgroundColor: colors.backgroundDefault, borderColor: colors.border },
            ]}
          >
            <ThemedText type="body" style={{ color: colors.textSecondary }}>
              R$
            </ThemedText>
            <TextInput
              style={[styles.input, { color: colors.text }]}
              placeholder="0,00"
              placeholderTextColor={colors.textSecondary}
              value={offer}
              onChangeText={setOffer}
              keyboardType="numeric"
            />
          </View>
        </View>

        <View style={styles.inputContainer}>
          <ThemedText type="small" style={styles.label}>
            Fotos da Demanda
          </ThemedText>
          <ThemedText type="caption" style={{ color: colors.textSecondary, marginBottom: Spacing.md }}>
            Adicione fotos para aumentar as chances de receber propostas
          </ThemedText>

          {photos.length > 0 && (
            <FlatList
              data={photos}
              renderItem={({ item, index }) => (
                <View style={styles.photoWrapper}>
                  <RNImage source={{ uri: item }} style={styles.photoThumbnail} />
                  <Pressable
                    style={[styles.removePhotoButton, { backgroundColor: colors.error }]}
                    onPress={() => handleRemovePhoto(index)}
                  >
                    <Feather name="x" size={16} color="#FFFFFF" />
                  </Pressable>
                </View>
              )}
              keyExtractor={(_, index) => `photo-${index}`}
              horizontal
              scrollEnabled={false}
              contentContainerStyle={styles.photosList}
              itemSeparatorComponent={() => <View style={{ width: Spacing.md }} />}
            />
          )}

          <Pressable
            style={[
              styles.photoButton,
              {
                backgroundColor: colors.primary,
                borderColor: colors.border,
                opacity: isPickingPhoto ? 0.6 : 1,
              },
            ]}
            onPress={handlePickPhoto}
            disabled={isPickingPhoto}
          >
            {isPickingPhoto ? (
              <ActivityIndicator color="#FFFFFF" size="small" />
            ) : (
              <>
                <Feather name="image" size={20} color="#FFFFFF" />
                <ThemedText type="small" style={{ color: '#FFFFFF', fontWeight: '600' }}>
                  Adicionar Foto ({photos.length})
                </ThemedText>
              </>
            )}
          </Pressable>
        </View>

        <View style={styles.inputContainer}>
          <ThemedText type="small" style={styles.label}>
            Descrição Detalhada *
          </ThemedText>
          <ThemedText type="caption" style={{ color: colors.accent, marginBottom: Spacing.md }}>
            💡 Dica: Quanto melhor a descrição, mais propostas você receberá
          </ThemedText>
          <View
            style={[
              styles.tipsBox,
              { backgroundColor: colors.accent + '10', borderColor: colors.accent },
            ]}
          >
            <Feather name="info" size={14} color={colors.accent} />
            <ThemedText type="caption" style={{ color: colors.accent, flex: 1 }}>
              Inclua: padrão desejado, requisitos especiais, urgência, contatos, referências
            </ThemedText>
          </View>
          <View
            style={[
              styles.textAreaWrapper,
              { backgroundColor: colors.backgroundDefault, borderColor: colors.border },
            ]}
          >
            <TextInput
              style={[styles.textArea, { color: colors.text }]}
              placeholder="Descreva a demanda com detalhes..."
              placeholderTextColor={colors.textSecondary}
              value={notes}
              onChangeText={setNotes}
              multiline
              numberOfLines={5}
              textAlignVertical="top"
            />
          </View>
        </View>

        <Button onPress={handleSubmit} disabled={isSubmitting} style={styles.submitButton}>
          {isSubmitting ? <ActivityIndicator color="#FFFFFF" /> : 'Publicar Demanda'}
        </Button>
      </ScreenKeyboardAwareScrollView>
    </ThemedView>
  );
}

const styles = StyleSheet.create({
