        <View style={styles.categoriesContainer}>
          <ThemedText type="h4" style={{ marginBottom: Spacing.md }}>
            Categorias
          </ThemedText>
          <View style={styles.categoryGrid}>
            {categories.map((cat) => (
              <Pressable
