import React, { useState } from 'react';
import { StyleSheet, View, Pressable, Linking } from 'react-native';
import { useSafeAreaInsets } from 'react-native-safe-area-context';
import { Feather } from '@expo/vector-icons';
import * as Haptics from 'expo-haptics';
import { ScreenScrollView } from '@/components/ScreenScrollView';
import { ThemedText } from '@/components/ThemedText';
import { ThemedView } from '@/components/ThemedView';
import { Button } from '@/components/Button';
import { useTheme } from '@/hooks/useTheme';
import { useAuth } from '@/hooks/useAuth';
import { Colors, Spacing, BorderRadius, Shadows } from '@/constants/theme';

interface FAQItem {
  id: string;
  question: string;
  answer: string;
  category: 'geral' | 'produtor' | 'trabalhador' | 'pagamento' | 'seguranca';
}

const faqItems: FAQItem[] = [
  {
    id: '1',
    category: 'geral',
    question: 'O que e o LidaCacau?',
    answer: 'O LidaCacau e um marketplace que conecta produtores de cacau com trabalhadores rurais qualificados em Uruara/PA. Aqui voce pode publicar demandas de servico, receber propostas, negociar termos e acompanhar o trabalho em tempo real.',
  },
  {
    id: '2',
    category: 'geral',
    question: 'O LidaCacau e gratuito?',
    answer: 'Sim! O cadastro e uso do app sao totalmente gratuitos. Nao cobramos taxas sobre os servicos contratados. O valor negociado entre produtor e trabalhador e o valor final.',
  },
  {
    id: '3',
    category: 'produtor',
    question: 'Como publico uma demanda de servico?',
    answer: 'Va na aba "Inicio" e toque no botao "+" no canto inferior. Selecione o tipo de servico, informe a quantidade, local e valor. Adicione fotos para melhorar a visibilidade. Trabalhadores da regiao receberao notificacao.',
  },
  {
    id: '4',
    category: 'produtor',
    question: 'Como escolho o melhor trabalhador?',
    answer: 'Ao receber propostas, avalie o nivel do trabalhador (N1 a N5), suas avaliacoes anteriores, preco proposto e se ele possui verificacao de identidade. Trabalhadores verificados passaram por validacao de documentos.',
  },
  {
    id: '5',
    category: 'trabalhador',
    question: 'Como subo de nivel?',
    answer: 'Seu nivel aumenta conforme acumula avaliacoes positivas. N1 (Iniciante): inicio. N2 (Experiente): 5+ avaliacoes, media 3.5+. N3 (Profissional): 10+ avaliacoes, media 4.0+. N4 (Especialista): 15+ avaliacoes, media 4.3+. N5 (Mestre): 20+ avaliacoes, media 4.5+.',
  },
  {
    id: '6',
    category: 'trabalhador',
    question: 'Como envio propostas?',
    answer: 'Na aba "Trabalhos", veja as demandas disponiveis na sua regiao. Toque em uma demanda para ver detalhes e clique em "Enviar Proposta". Informe seu preco e uma mensagem explicando sua experiencia.',
  },
  {
    id: '7',
    category: 'pagamento',
    question: 'Como funciona o pagamento?',
    answer: 'O pagamento e combinado diretamente entre produtor e trabalhador. Voces podem negociar: 100% apos conclusao, 50/50, por unidade, por hora ou por diaria. O app facilita a negociacao mas nao intermedia o dinheiro.',
  },
  {
    id: '8',
    category: 'pagamento',
    question: 'Preciso emitir nota fiscal?',
    answer: 'Se voce atua como MEI ou empresa, recomendamos emitir NFS-e. O app tem integracao com o portal da Prefeitura de Uruara. Acesse pelo menu do Perfil > Nota Fiscal Eletronica.',
  },
  {
    id: '9',
    category: 'seguranca',
    question: 'O que e a verificacao de identidade?',
    answer: 'A verificacao de identidade confirma que o usuario e quem diz ser. Enviamos selfie + documento (RG/CNH). Usuarios verificados tem um selo verde no perfil, aumentando a confianca.',
  },
  {
    id: '10',
    category: 'seguranca',
    question: 'O contrato de empreitada tem validade legal?',
    answer: 'Sim! O contrato gerado pelo app segue as normas brasileiras para contratos de empreitada. Ambas as partes assinam digitalmente. Guarde uma copia como comprovante do acordo.',
  },
];

const categoryLabels: Record<string, string> = {
  geral: 'Geral',
  produtor: 'Produtores',
  trabalhador: 'Trabalhadores',
  pagamento: 'Pagamento',
  seguranca: 'Seguranca',
};

const WHATSAPP_SUPPORT = 'https://wa.me/5593999999999?text=Ola!%20Preciso%20de%20ajuda%20com%20o%20LidaCacau.';

export default function FAQSupportScreen() {
  const { theme, isDark } = useTheme();
  const { user } = useAuth();
  const insets = useSafeAreaInsets();
  const colors = isDark ? Colors.dark : Colors.light;

  const [expandedId, setExpandedId] = useState<string | null>(null);
  const [selectedCategory, setSelectedCategory] = useState<string | null>(null);

  const filteredFaq = selectedCategory
    ? faqItems.filter((item) => item.category === selectedCategory)
    : faqItems;

  const toggleExpand = (id: string) => {
    Haptics.selectionAsync();
    setExpandedId(expandedId === id ? null : id);
  };

  const handleWhatsAppSupport = () => {
    Haptics.impactAsync(Haptics.ImpactFeedbackStyle.Medium);
    Linking.openURL(WHATSAPP_SUPPORT);
  };

  const categories = Object.keys(categoryLabels);

  return (
    <ThemedView style={styles.container}>
      <ScreenScrollView
        contentContainerStyle={[
          styles.scrollContent,
          { paddingTop: Spacing.xl, paddingBottom: insets.bottom + Spacing['2xl'] },
        ]}
      >
        <View style={[styles.headerCard, { backgroundColor: colors.primary + '15' }]}>
          <View style={[styles.iconCircle, { backgroundColor: colors.primary }]}>
            <Feather name="help-circle" size={32} color="#FFFFFF" />
          </View>
          <ThemedText type="h3" style={{ textAlign: 'center', marginTop: Spacing.md }}>
            Central de Ajuda
          </ThemedText>
          <ThemedText type="body" style={{ textAlign: 'center', color: colors.textSecondary, marginTop: Spacing.sm }}>
            Encontre respostas para suas duvidas ou fale com nosso suporte
          </ThemedText>
        </View>

        <View style={[styles.supportCard, { backgroundColor: colors.success + '15' }, Shadows.card]}>
          <View style={styles.supportContent}>
            <View style={[styles.supportIcon, { backgroundColor: '#25D366' }]}>
              <Feather name="message-circle" size={24} color="#FFFFFF" />
            </View>
            <View style={{ flex: 1, marginLeft: Spacing.md }}>
              <ThemedText type="body" style={{ fontWeight: '700' }}>
                Suporte via WhatsApp
              </ThemedText>
              <ThemedText type="small" style={{ color: colors.textSecondary, marginTop: Spacing.xs }}>
                Atendimento de segunda a sexta, 8h as 18h
              </ThemedText>
            </View>
          </View>
          <Button onPress={handleWhatsAppSupport} style={{ marginTop: Spacing.md }}>
            <View style={styles.whatsappButton}>
              <Feather name="message-circle" size={18} color="#FFFFFF" />
              <ThemedText type="body" style={{ color: '#FFFFFF', fontWeight: '600', marginLeft: Spacing.sm }}>
                Falar com Suporte
              </ThemedText>
            </View>
          </Button>
        </View>

        <View style={styles.categoriesContainer}>
          <ThemedText type="h4" style={{ marginBottom: Spacing.md }}>
            Filtrar por Categoria
          </ThemedText>
          <View style={styles.categoryRow}>
            <Pressable
              style={[
                styles.categoryChip,
                {
                  backgroundColor: selectedCategory === null ? colors.primary : colors.backgroundSecondary,
                  borderColor: colors.primary,
                },
              ]}
              onPress={() => {
                Haptics.selectionAsync();
                setSelectedCategory(null);
              }}
            >
              <ThemedText
                type="small"
                style={{
                  color: selectedCategory === null ? '#FFFFFF' : colors.text,
                  fontWeight: '600',
                }}
              >
                Todas
              </ThemedText>
            </Pressable>
            {categories.map((cat) => (
              <Pressable
                key={cat}
                style={[
                  styles.categoryChip,
                  {
                    backgroundColor: selectedCategory === cat ? colors.primary : colors.backgroundSecondary,
                    borderColor: colors.primary,
                  },
                ]}
                onPress={() => {
                  Haptics.selectionAsync();
                  setSelectedCategory(cat);
                }}
              >
                <ThemedText
                  type="small"
                  style={{
                    color: selectedCategory === cat ? '#FFFFFF' : colors.text,
                    fontWeight: '600',
                  }}
                >
                  {categoryLabels[cat]}
                </ThemedText>
              </Pressable>
            ))}
          </View>
        </View>

        <View style={styles.faqContainer}>
          <ThemedText type="h4" style={{ marginBottom: Spacing.md }}>
            Perguntas Frequentes ({filteredFaq.length})
          </ThemedText>

          {filteredFaq.map((item) => (
            <Pressable
              key={item.id}
              style={[styles.faqItem, { backgroundColor: colors.card }, Shadows.card]}
              onPress={() => toggleExpand(item.id)}
            >
              <View style={styles.faqHeader}>
                <View style={[styles.categoryDot, { backgroundColor: colors.primary }]} />
                <ThemedText type="body" style={{ flex: 1, fontWeight: '600' }}>
                  {item.question}
                </ThemedText>
                <Feather
                  name={expandedId === item.id ? 'chevron-up' : 'chevron-down'}
                  size={20}
                  color={colors.textSecondary}
                />
              </View>
              {expandedId === item.id && (
                <View style={styles.faqAnswer}>
                  <ThemedText type="body" style={{ color: colors.textSecondary, lineHeight: 22 }}>
                    {item.answer}
                  </ThemedText>
                </View>
              )}
            </Pressable>
          ))}
        </View>

        <View style={[styles.infoCard, { backgroundColor: colors.backgroundSecondary }]}>
          <Feather name="mail" size={20} color={colors.primary} />
          <View style={{ flex: 1, marginLeft: Spacing.md }}>
            <ThemedText type="body" style={{ fontWeight: '600' }}>
              Nao encontrou sua resposta?
            </ThemedText>
            <ThemedText type="small" style={{ color: colors.textSecondary, marginTop: Spacing.xs }}>
              Entre em contato pelo WhatsApp ou envie email para suporte@lidacacau.app
            </ThemedText>
          </View>
        </View>
      </ScreenScrollView>
    </ThemedView>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
  },
  scrollContent: {
    paddingHorizontal: Spacing.lg,
  },
  headerCard: {
    padding: Spacing.xl,
    borderRadius: BorderRadius.lg,
    alignItems: 'center',
    marginBottom: Spacing.lg,
  },
  iconCircle: {
    width: 72,
    height: 72,
    borderRadius: 36,
    justifyContent: 'center',
    alignItems: 'center',
  },
  supportCard: {
    padding: Spacing.lg,
    borderRadius: BorderRadius.lg,
    marginBottom: Spacing.lg,
  },
  supportContent: {
    flexDirection: 'row',
    alignItems: 'center',
  },
  supportIcon: {
    width: 48,
    height: 48,
    borderRadius: 24,
    justifyContent: 'center',
    alignItems: 'center',
  },
  whatsappButton: {
    flexDirection: 'row',
    alignItems: 'center',
    justifyContent: 'center',
  },
  categoriesContainer: {
    marginBottom: Spacing.lg,
  },
  categoryRow: {
    flexDirection: 'row',
    flexWrap: 'wrap',
    gap: Spacing.sm,
  },
  categoryChip: {
    paddingHorizontal: Spacing.md,
    paddingVertical: Spacing.sm,
    borderRadius: BorderRadius.full,
    borderWidth: 1,
  },
  faqContainer: {
    marginBottom: Spacing.lg,
  },
  faqItem: {
    padding: Spacing.lg,
    borderRadius: BorderRadius.lg,
    marginBottom: Spacing.md,
  },
  faqHeader: {
    flexDirection: 'row',
    alignItems: 'center',
  },
  categoryDot: {
    width: 8,
    height: 8,
    borderRadius: 4,
    marginRight: Spacing.md,
  },
  faqAnswer: {
    marginTop: Spacing.md,
    paddingTop: Spacing.md,
    borderTopWidth: 1,
    borderTopColor: 'rgba(0,0,0,0.05)',
  },
  infoCard: {
    flexDirection: 'row',
    alignItems: 'flex-start',
    padding: Spacing.lg,
    borderRadius: BorderRadius.lg,
  },
});

screens/shared/PortfolioScreen.tsx.content:
import React, { useState, useEffect, useCallback } from 'react';
import { StyleSheet, View, Pressable, Alert, FlatList, Dimensions } from 'react-native';
import { useSafeAreaInsets } from 'react-native-safe-area-context';
import { useFocusEffect } from '@react-navigation/native';
import { Feather } from '@expo/vector-icons';
import * as ImagePicker from 'expo-image-picker';
import * as Haptics from 'expo-haptics';
import { Image } from 'expo-image';
import { ThemedText } from '@/components/ThemedText';
import { ThemedView } from '@/components/ThemedView';
import { Button } from '@/components/Button';
import { useTheme } from '@/hooks/useTheme';
import { useAuth } from '@/hooks/useAuth';
import { Colors, Spacing, BorderRadius, Shadows } from '@/constants/theme';
import { updateUser } from '@/utils/storage';
import { PortfolioItem } from '@/types';

const { width: SCREEN_WIDTH } = Dimensions.get('window');
const IMAGE_SIZE = (SCREEN_WIDTH - Spacing.lg * 2 - Spacing.sm * 2) / 3;

export default function PortfolioScreen() {
  const { theme, isDark } = useTheme();
  const { user, refreshUser } = useAuth();
  const insets = useSafeAreaInsets();
  const colors = isDark ? Colors.dark : Colors.light;

  const [portfolio, setPortfolio] = useState<PortfolioItem[]>([]);
  const [isUploading, setIsUploading] = useState(false);
  const [selectedImage, setSelectedImage] = useState<PortfolioItem | null>(null);

  useFocusEffect(
    useCallback(() => {
      if (user?.portfolio) {
        setPortfolio(user.portfolio);
      }
    }, [user])
  );

  const handleAddPhoto = async () => {
    const permission = await ImagePicker.requestMediaLibraryPermissionsAsync();
    if (!permission.granted) {
      Alert.alert('Permissao Necessaria', 'Precisamos de acesso a galeria para adicionar fotos.');
      return;
    }

    const result = await ImagePicker.launchImageLibraryAsync({
      mediaTypes: 'images',
      allowsEditing: true,
      aspect: [4, 3],
      quality: 0.8,
    });

    if (!result.canceled && result.assets[0]) {
      setIsUploading(true);
      try {
        const newItem: PortfolioItem = {
          id: Date.now().toString(),
          photoUri: result.assets[0].uri,
          description: '',
          serviceTypeId: '',
          createdAt: new Date().toISOString(),
        };

        const updatedPortfolio = [...portfolio, newItem];
        setPortfolio(updatedPortfolio);

        if (user) {
          await updateUser(user.id, { portfolio: updatedPortfolio });
          await refreshUser();
        }

        Haptics.notificationAsync(Haptics.NotificationFeedbackType.Success);
        Alert.alert('Sucesso!', 'Foto adicionada ao portfolio.');
      } catch (error) {
        Alert.alert('Erro', 'Nao foi possivel adicionar a foto.');
      } finally {
        setIsUploading(false);
      }
    }
  };

  const handleDeletePhoto = async (itemId: string) => {
    Alert.alert(
      'Remover Foto',
      'Deseja remover esta foto do seu portfolio?',
      [
        { text: 'Cancelar', style: 'cancel' },
        {
          text: 'Remover',
          style: 'destructive',
          onPress: async () => {
            const updatedPortfolio = portfolio.filter((p) => p.id !== itemId);
            setPortfolio(updatedPortfolio);
            setSelectedImage(null);

            if (user) {
              await updateUser(user.id, { portfolio: updatedPortfolio });
              await refreshUser();
            }

            Haptics.notificationAsync(Haptics.NotificationFeedbackType.Success);
          },
        },
      ]
    );
  };

  const renderPortfolioItem = ({ item }: { item: PortfolioItem }) => (
    <Pressable
      style={styles.imageContainer}
      onPress={() => setSelectedImage(item)}
    >
      <Image
        source={{ uri: item.photoUri }}
        style={styles.image}
        contentFit="cover"
      />
    </Pressable>
  );

  return (
    <ThemedView style={styles.container}>
      <View style={[styles.header, { paddingTop: Spacing.xl }]}>
        <View style={[styles.statsCard, { backgroundColor: colors.card }, Shadows.card]}>
          <View style={styles.statItem}>
            <ThemedText type="h2" style={{ color: colors.primary }}>
              {portfolio.length}
            </ThemedText>
            <ThemedText type="small" style={{ color: colors.textSecondary }}>
              Fotos
            </ThemedText>
          </View>
          <View style={[styles.statDivider, { backgroundColor: colors.border }]} />
          <View style={styles.statItem}>
            <ThemedText type="body" style={{ color: colors.textSecondary }}>
              Maximo
            </ThemedText>
            <ThemedText type="h3" style={{ color: colors.text }}>
              20
            </ThemedText>
          </View>
        </View>

        <Button
          onPress={handleAddPhoto}
          disabled={isUploading || portfolio.length >= 20}
          style={{ marginTop: Spacing.lg, width: '100%' }}
        >
          <View style={styles.addButtonContent}>
            <Feather name="plus" size={18} color="#FFFFFF" />
            <ThemedText type="body" style={{ color: '#FFFFFF', fontWeight: '600', marginLeft: Spacing.sm }}>
              {isUploading ? 'Enviando...' : 'Adicionar Foto'}
            </ThemedText>
          </View>
        </Button>
      </View>

      {portfolio.length === 0 ? (
        <View style={styles.emptyState}>
          <View style={[styles.emptyIcon, { backgroundColor: colors.backgroundSecondary }]}>
            <Feather name="image" size={48} color={colors.textSecondary} />
          </View>
          <ThemedText type="h4" style={{ marginTop: Spacing.lg, textAlign: 'center' }}>
            Seu Portfolio esta Vazio
          </ThemedText>
          <ThemedText type="body" style={{ color: colors.textSecondary, textAlign: 'center', marginTop: Spacing.sm }}>
            Adicione fotos dos seus trabalhos para mostrar suas habilidades aos produtores.
          </ThemedText>
        </View>
      ) : (
        <FlatList
          data={portfolio}
          renderItem={renderPortfolioItem}
          keyExtractor={(item) => item.id}
          numColumns={3}
          contentContainerStyle={styles.grid}
          showsVerticalScrollIndicator={false}
        />
      )}

      {selectedImage && (
        <Pressable
          style={[styles.modal, { backgroundColor: 'rgba(0,0,0,0.9)' }]}
          onPress={() => setSelectedImage(null)}
        >
          <Pressable 
            style={styles.closeButton} 
            onPress={() => setSelectedImage(null)}
          >
            <Feather name="x" size={24} color="#FFFFFF" />
          </Pressable>
          <Image
            source={{ uri: selectedImage.photoUri }}
            style={styles.fullImage}
            contentFit="contain"
          />
          <View style={styles.modalActions}>
            <Pressable
              style={[styles.deleteButton, { backgroundColor: colors.error }]}
              onPress={() => handleDeletePhoto(selectedImage.id)}
            >
              <Feather name="trash-2" size={18} color="#FFFFFF" />
              <ThemedText type="body" style={{ color: '#FFFFFF', marginLeft: Spacing.sm }}>
                Remover
              </ThemedText>
            </Pressable>
          </View>
          <ThemedText type="small" style={{ color: 'rgba(255,255,255,0.6)', marginTop: Spacing.md }}>
            Adicionada em {new Date(selectedImage.createdAt).toLocaleDateString('pt-BR')}
          </ThemedText>
        </Pressable>
      )}

      <View style={[styles.tipCard, { backgroundColor: colors.backgroundSecondary, marginBottom: insets.bottom + Spacing.lg }]}>
        <Feather name="info" size={18} color={colors.primary} />
        <ThemedText type="small" style={{ flex: 1, color: colors.textSecondary, marginLeft: Spacing.sm }}>
          Dica: Adicione fotos de antes e depois dos seus trabalhos para impressionar os produtores!
        </ThemedText>
      </View>
    </ThemedView>
  );
}

