                <Image source={{ uri: user.avatar }} style={styles.avatar} contentFit="cover" />
              ) : (
                <View style={[styles.avatarPlaceholder, { backgroundColor: colors.primary }]}>
                  <ThemedText type="h1" style={{ color: '#FFFFFF' }}>
                    {user?.name?.charAt(0) || 'U'}
