              ) : null}
            </View>
            <ThemedText type="body" style={{ color: colors.textSecondary }}>
              {user?.email}
            </ThemedText>

            <View style={styles.badges}>
              {isWorker && user?.level ? (
                <View style={[styles.levelBadge, { backgroundColor: levelColor }]}>
                  <Feather name="award" size={14} color="#FFFFFF" />
