utils/sharing.ts.content:
import * as Sharing from 'expo-sharing';
import * as Linking from 'expo-linking';
import * as Clipboard from 'expo-clipboard';
import { Platform, Share } from 'react-native';
import { Job, ServiceOffer, User } from '@/types';
import { getServiceTypeById } from '@/data/serviceTypes';
import { formatCurrency, formatDate } from '@/utils/format';

type CardShareType = 'demand' | 'offer';

interface ShareContent {
  title: string;
  message: string;
  url?: string;
}

export const generateJobShareText = (job: Job, producerName?: string): ShareContent => {
  const serviceType = getServiceTypeById(job.serviceTypeId);
  const serviceName = serviceType?.name || 'Servico';
  const unit = serviceType?.unit || 'un';

  const lines = [
    `DEMANDA DE TRABALHO`,
    ``,
    `Servico: ${serviceName}`,
    `Valor: ${formatCurrency(job.offer)}`,
    `Local: ${job.locationText}`,
    job.quantity ? `Quantidade: ${job.quantity} ${unit}` : '',
    job.notes ? `Detalhes: ${job.notes}` : '',
    ``,
    producerName ? `Publicado por: ${producerName}` : '',
    ``,
    `Interessado? Baixe o LidaCacau e envie sua proposta!`,
    ``,
    `#LidaCacau #TrabalhoRural #${serviceName.replace(/\s+/g, '')}`,
  ].filter(Boolean);

  return {
    title: `Demanda: ${serviceName}`,
    message: lines.join('\n'),
  };
};

export const generateOfferShareText = (offer: ServiceOffer, workerName?: string): ShareContent => {
  const serviceNames = offer.serviceTypeIds
    .map(id => getServiceTypeById(id)?.name)
    .filter(Boolean)
    .join(', ');

  const price = offer.pricePerDay 
    ? `${formatCurrency(offer.pricePerDay)}/dia` 
    : offer.pricePerHour 
    ? `${formatCurrency(offer.pricePerHour)}/hora`
    : offer.pricePerUnit
    ? `${formatCurrency(offer.pricePerUnit)}/unidade`
    : 'A combinar';

  const extras: string[] = [];
  if (offer.extras?.providesFood) extras.push('Alimentacao');
  if (offer.extras?.providesAccommodation) extras.push('Estadia');
  if (offer.extras?.providesTransport) extras.push('Transporte');
  if (offer.extras?.toolsProvided) extras.push('Ferramentas');

  const lines = [
    `TRABALHADOR DISPONIVEL`,
    ``,
    `Servicos: ${serviceNames}`,
    `Valor: ${price}`,
    offer.priceNegotiable ? `(Valor negociavel)` : '',
    `Raio de atendimento: ${offer.availableRadius}km`,
    offer.description ? `Sobre: ${offer.description}` : '',
    extras.length > 0 ? `Oferece: ${extras.join(', ')}` : '',
    ``,
    workerName ? `Trabalhador: ${workerName}` : '',
    ``,
    `Precisa de ajuda? Baixe o LidaCacau e entre em contato!`,
    ``,
    `#LidaCacau #TrabalhoRural #${serviceNames.replace(/\s+/g, '').replace(/,/g, ' #')}`,
  ].filter(Boolean);

  return {
    title: `Oferta: ${serviceNames}`,
    message: lines.join('\n'),
  };
};

export const shareViaWhatsApp = async (content: ShareContent): Promise<boolean> => {
  try {
    const encodedMessage = encodeURIComponent(content.message);
    const whatsappUrl = `whatsapp://send?text=${encodedMessage}`;

    const canOpen = await Linking.canOpenURL(whatsappUrl);
    if (canOpen) {
      await Linking.openURL(whatsappUrl);
      return true;
    } else {
      const webWhatsapp = `https://wa.me/?text=${encodedMessage}`;
      await Linking.openURL(webWhatsapp);
      return true;
    }
  } catch (error) {
    console.error('Error sharing via WhatsApp:', error);
    return false;
  }
};

export const shareViaSystem = async (content: ShareContent): Promise<boolean> => {
  try {
    const result = await Share.share({
      message: content.message,
      title: content.title,
    });

    return result.action === Share.sharedAction;
  } catch (error) {
    console.error('Error sharing:', error);
    return false;
  }
};

export const shareCard = async (
  type: CardShareType,
  data: Job | ServiceOffer,
  ownerName?: string,
  method: 'whatsapp' | 'system' = 'system'
): Promise<boolean> => {
  const content = type === 'demand' 
    ? generateJobShareText(data as Job, ownerName)
    : generateOfferShareText(data as ServiceOffer, ownerName);

  if (method === 'whatsapp') {
    return shareViaWhatsApp(content);
  }

  return shareViaSystem(content);
};

export const copyCardLink = async (type: CardShareType, id: string): Promise<boolean> => {
  try {
    const baseUrl = 'https://lidacacau.app';
    const path = type === 'demand' ? 'demanda' : 'oferta';
    const url = `${baseUrl}/${path}/${id}`;

    await Clipboard.setStringAsync(url);
    return true;
  } catch (error) {
    console.error('Error copying card link:', error);
    return false;
  }
};

export const generateCardLink = (type: CardShareType, id: string): string => {
  const baseUrl = 'https://lidacacau.app';
  const path = type === 'demand' ? 'demanda' : 'oferta';
  return `${baseUrl}/${path}/${id}`;
};

utils/analytics.ts.content:
import { Platform } from 'react-native';
import AsyncStorage from '@react-native-async-storage/async-storage';
import { AnalyticsEvent, AnalyticsEventType, AnalyticsSession } from '@/types';

const STORAGE_KEY = '@lidacacau_analytics';
const SESSION_KEY = '@lidacacau_analytics_session';
const MAX_EVENTS = 1000;

let currentSession: AnalyticsSession | null = null;

const generateId = (): string => {
  return Date.now().toString(36) + Math.random().toString(36).substr(2);
};

const getDeviceInfo = (): { platform: 'ios' | 'android' | 'web'; version?: string } => {
  const platformOS = Platform.OS;
  let platform: 'ios' | 'android' | 'web' = 'web';

  if (platformOS === 'ios') {
    platform = 'ios';
  } else if (platformOS === 'android') {
    platform = 'android';
  }

  return {
    platform,
    version: Platform.Version?.toString(),
  };
};

export const trackEvent = async (
  eventType: AnalyticsEventType,
  eventData?: Record<string, unknown>,
  screen?: string
): Promise<AnalyticsEvent | null> => {
  try {
    const event: AnalyticsEvent = {
      id: generateId(),
      eventType,
      eventData,
      screen,
      timestamp: new Date().toISOString(),
      sessionId: currentSession?.id,
      deviceInfo: getDeviceInfo(),
    };

    const existingData = await AsyncStorage.getItem(STORAGE_KEY);
    let events: AnalyticsEvent[] = existingData ? JSON.parse(existingData) : [];

    events.push(event);

    if (events.length > MAX_EVENTS) {
      events = events.slice(events.length - MAX_EVENTS);
    }

    await AsyncStorage.setItem(STORAGE_KEY, JSON.stringify(events));

    if (currentSession) {
      currentSession.events.push(event);
      if (screen && !currentSession.screens.includes(screen)) {
        currentSession.screens.push(screen);
      }
      await AsyncStorage.setItem(SESSION_KEY, JSON.stringify(currentSession));
    }

    return event;
  } catch (error) {
    console.error('Error tracking event:', error);
    return null;
  }
};

export const startSession = async (userId?: string): Promise<AnalyticsSession> => {
  try {
    if (currentSession) {
      await endSession();
    }

    const session: AnalyticsSession = {
      id: generateId(),
      userId,
      startedAt: new Date().toISOString(),
      events: [],
      screens: [],
    };

    currentSession = session;
    await AsyncStorage.setItem(SESSION_KEY, JSON.stringify(session));

    await trackEvent('app_open');

    return session;
  } catch (error) {
    console.error('Error starting session:', error);
    const fallbackSession: AnalyticsSession = {
      id: generateId(),
      startedAt: new Date().toISOString(),
      events: [],
      screens: [],
    };
    currentSession = fallbackSession;
    return fallbackSession;
  }
};

export const endSession = async (): Promise<AnalyticsSession | null> => {
  try {
    if (!currentSession) {
      return null;
    }

    currentSession.endedAt = new Date().toISOString();
    await AsyncStorage.setItem(SESSION_KEY, JSON.stringify(currentSession));

    const endedSession = { ...currentSession };
    currentSession = null;

    return endedSession;
  } catch (error) {
    console.error('Error ending session:', error);
    currentSession = null;
    return null;
  }
};

export const getAnalyticsEvents = async (limit?: number): Promise<AnalyticsEvent[]> => {
  try {
    const data = await AsyncStorage.getItem(STORAGE_KEY);
    let events: AnalyticsEvent[] = data ? JSON.parse(data) : [];

    if (limit && limit > 0) {
      events = events.slice(-limit);
    }

    return events;
  } catch (error) {
    console.error('Error getting analytics events:', error);
    return [];
  }
};

export const clearAnalyticsEvents = async (): Promise<boolean> => {
  try {
    await AsyncStorage.removeItem(STORAGE_KEY);
    return true;
  } catch (error) {
    console.error('Error clearing analytics events:', error);
    return false;
  }
};

export const getCurrentSession = (): AnalyticsSession | null => {
  return currentSession;
};

export const getSessionFromStorage = async (): Promise<AnalyticsSession | null> => {
  try {
    const data = await AsyncStorage.getItem(SESSION_KEY);
    return data ? JSON.parse(data) : null;
  } catch (error) {
    console.error('Error getting session from storage:', error);
    return null;
  }
};

export const setSessionUserId = async (userId: string): Promise<void> => {
  try {
    if (currentSession) {
      currentSession.userId = userId;
      await AsyncStorage.setItem(SESSION_KEY, JSON.stringify(currentSession));
    }
  } catch (error) {
    console.error('Error setting session user ID:', error);
  }
};

export const getEventsByType = async (
  eventType: AnalyticsEventType,
  limit?: number
): Promise<AnalyticsEvent[]> => {
  try {
    const allEvents = await getAnalyticsEvents();
    let filteredEvents = allEvents.filter((e) => e.eventType === eventType);

    if (limit && limit > 0) {
      filteredEvents = filteredEvents.slice(-limit);
    }

    return filteredEvents;
  } catch (error) {
    console.error('Error getting events by type:', error);
    return [];
  }
};

export const getEventsByScreen = async (
  screen: string,
  limit?: number
): Promise<AnalyticsEvent[]> => {
  try {
    const allEvents = await getAnalyticsEvents();
    let filteredEvents = allEvents.filter((e) => e.screen === screen);

    if (limit && limit > 0) {
      filteredEvents = filteredEvents.slice(-limit);
    }

    return filteredEvents;
  } catch (error) {
    console.error('Error getting events by screen:', error);
    return [];
  }
};

export const getEventsCount = async (): Promise<number> => {
  try {
    const events = await getAnalyticsEvents();
    return events.length;
  } catch (error) {
    console.error('Error getting events count:', error);
    return 0;
  }
};

screens/shared/UserSearchScreen.tsx.content:
import React, { useState, useCallback } from 'react';
import {
  StyleSheet,
  View,
  Pressable,
  TextInput,
  FlatList,
  RefreshControl,
  Alert,
  ActivityIndicator,
  ScrollView,
  Platform,
} from 'react-native';
import { useNavigation, useFocusEffect } from '@react-navigation/native';
import { NativeStackNavigationProp } from '@react-navigation/native-stack';
import { Feather } from '@expo/vector-icons';
import { Image } from 'expo-image';
import * as Haptics from 'expo-haptics';
import { ThemedView } from '@/components/ThemedView';
import { ThemedText } from '@/components/ThemedText';
import { useTheme } from '@/hooks/useTheme';
import { useAuth } from '@/hooks/useAuth';
import { useScreenInsets } from '@/hooks/useScreenInsets';
import { Colors, Spacing, BorderRadius, Shadows, LevelColors, Typography } from '@/constants/theme';
import { User, FriendConnection, UserPresence } from '@/types';
import {
  getUsers,
  getFriends,
  getSentFriendRequests,
  sendFriendRequest,
  getUserPresence,
} from '@/utils/storage';
import { trackEvent } from '@/utils/analytics';
import { RootStackParamList } from '@/navigation/RootNavigator';

type FilterType = 'all' | 'producers' | 'workers' | 'verified' | 'online';

interface UserWithStatus extends User {
  isFriend: boolean;
  hasPendingRequest: boolean;
  isOnline: boolean;
}

const FILTERS: { key: FilterType; label: string }[] = [
  { key: 'all', label: 'Todos' },
  { key: 'producers', label: 'Produtores' },
  { key: 'workers', label: 'Trabalhadores' },
  { key: 'verified', label: 'Verificados' },
  { key: 'online', label: 'Online agora' },
];

export default function UserSearchScreen() {
  const navigation = useNavigation<NativeStackNavigationProp<RootStackParamList>>();
  const { theme, isDark } = useTheme();
  const { user } = useAuth();
  const { paddingTop, paddingBottom } = useScreenInsets();
  const colors = isDark ? Colors.dark : Colors.light;

  const [searchQuery, setSearchQuery] = useState('');
  const [activeFilter, setActiveFilter] = useState<FilterType>('all');
  const [allUsers, setAllUsers] = useState<UserWithStatus[]>([]);
  const [loading, setLoading] = useState(true);
  const [refreshing, setRefreshing] = useState(false);
  const [sendingRequest, setSendingRequest] = useState<string | null>(null);

  const loadUsers = useCallback(async () => {
    if (!user) return;

    try {
      const [users, friendConnections, sentRequests] = await Promise.all([
        getUsers(),
        getFriends(user.id),
        getSentFriendRequests(user.id),
      ]);

      const friendIds = new Set<string>();
      for (const connection of friendConnections) {
        const friendId = connection.requesterId === user.id 
          ? connection.receiverId 
          : connection.requesterId;
        friendIds.add(friendId);
      }

      const pendingRequestIds = new Set<string>();
      for (const request of sentRequests) {
        pendingRequestIds.add(request.receiverId);
      }

      const usersWithPresence: UserWithStatus[] = [];

      for (const u of users) {
        if (u.id === user.id) continue;

        const presence = await getUserPresence(u.id);
        const isOnline = presence?.isOnline || false;

        usersWithPresence.push({
          ...u,
          isFriend: friendIds.has(u.id),
          hasPendingRequest: pendingRequestIds.has(u.id),
          isOnline,
        });
      }

      setAllUsers(usersWithPresence);
    } catch (error) {
      console.error('Error loading users:', error);
    } finally {
      setLoading(false);
      setRefreshing(false);
    }
  }, [user]);

  useFocusEffect(
    useCallback(() => {
      loadUsers();
    }, [loadUsers])
  );

  const handleRefresh = useCallback(() => {
    setRefreshing(true);
    loadUsers();
  }, [loadUsers]);

  const handleSendRequest = async (targetUserId: string, targetUserName: string) => {
    if (!user) return;

    setSendingRequest(targetUserId);
    try {
      await sendFriendRequest(user.id, targetUserId);
      await trackEvent('friend_request_send', { targetUserId });
      if (Platform.OS !== 'web') {
        Haptics.notificationAsync(Haptics.NotificationFeedbackType.Success);
      }
      Alert.alert(
        'Pedido Enviado!',
        `Voce deu a mao para ${targetUserName}. Aguarde a resposta!`
      );
      await loadUsers();
    } catch (error: any) {
      Alert.alert('Erro', error.message || 'Nao foi possivel enviar o pedido');
    } finally {
      setSendingRequest(null);
    }
  };

  const handleNavigateToProfile = (userId: string) => {
    navigation.navigate('OtherUserProfile', { userId });
  };

  const filteredUsers = allUsers.filter((u) => {
    if (searchQuery.trim()) {
      const query = searchQuery.toLowerCase().trim();
      if (!u.name.toLowerCase().includes(query)) {
        return false;
      }
    }

    switch (activeFilter) {
      case 'producers':
        return u.roles.includes('producer');
      case 'workers':
        return u.roles.includes('worker');
      case 'verified':
        return u.verification?.status === 'approved';
      case 'online':
        return u.isOnline;
      case 'all':
      default:
        return true;
    }
  });

  const getInitials = (name: string): string => {
    const parts = name.trim().split(' ');
    if (parts.length === 1) {
      return parts[0].substring(0, 2).toUpperCase();
    }
    return (parts[0][0] + parts[parts.length - 1][0]).toUpperCase();
  };

  const renderAvatar = (u: UserWithStatus) => {
    if (u.avatar) {
      return (
        <View style={styles.avatarContainer}>
          <Image
            source={{ uri: u.avatar }}
            style={styles.avatar}
            contentFit="cover"
          />
          {u.isOnline ? (
            <View style={[styles.onlineIndicator, { backgroundColor: colors.success }]} />
          ) : null}
        </View>
      );
    }
    return (
      <View style={styles.avatarContainer}>
        <View style={[styles.avatarPlaceholder, { backgroundColor: colors.primary + '30' }]}>
          <ThemedText type="h4" style={{ color: colors.primary }}>
            {getInitials(u.name)}
          </ThemedText>
        </View>
        {u.isOnline ? (
          <View style={[styles.onlineIndicator, { backgroundColor: colors.success }]} />
        ) : null}
      </View>
    );
  };

  const renderLevelBadge = (level: number | undefined, role: string) => {
    if (!level) return null;
    const levelKey = `N${level}` as keyof typeof LevelColors;
    return (
      <View style={[styles.levelBadge, { backgroundColor: LevelColors[levelKey] }]}>
        <ThemedText type="small" style={styles.levelText}>
          N{level}
        </ThemedText>
      </View>
    );
  };

  const renderRating = (rating: number | undefined, totalReviews: number | undefined) => {
    const displayRating = rating || 0;
    const stars = Math.round(displayRating);

    return (
      <View style={styles.ratingContainer}>
        {Array.from({ length: 5 }).map((_, i) => (
          <Feather
            key={i}
            name="star"
            size={12}
            color={i < stars ? '#FFB800' : colors.textSecondary}
          />
        ))}
        <ThemedText type="small" style={{ color: colors.textSecondary, marginLeft: Spacing.xs }}>
          {Number(displayRating || 0).toFixed(1)} ({totalReviews || 0})
        </ThemedText>
      </View>
    );
  };

  const renderActionButton = (u: UserWithStatus) => {
    if (u.isFriend) {
      return (
        <View style={[styles.actionButton, styles.friendButton, { backgroundColor: colors.textSecondary + '30' }]}>
          <Feather name="check" size={16} color={colors.textSecondary} />
          <ThemedText type="small" style={{ color: colors.textSecondary }}>
            Ja amigos
          </ThemedText>
        </View>
      );
    }

    if (u.hasPendingRequest) {
      return (
        <View style={[styles.actionButton, { backgroundColor: colors.warning }]}>
          <Feather name="clock" size={16} color="#FFFFFF" />
          <ThemedText type="small" style={{ color: '#FFFFFF' }}>
            Aguardando...
          </ThemedText>
        </View>
      );
    }

    return (
      <Pressable
        style={[styles.actionButton, { backgroundColor: colors.handshake }]}
        onPress={() => handleSendRequest(u.id, u.name)}
        disabled={sendingRequest === u.id}
      >
        {sendingRequest === u.id ? (
          <ActivityIndicator size="small" color="#FFFFFF" />
        ) : (
          <>
            <Feather name="user-plus" size={16} color="#FFFFFF" />
            <ThemedText type="small" style={{ color: '#FFFFFF', fontWeight: '600' }}>
              Dar a Mao
            </ThemedText>
          </>
        )}
      </Pressable>
    );
  };

  const renderUserCard = ({ item }: { item: UserWithStatus }) => (
    <Pressable
      style={[styles.userCard, { backgroundColor: colors.card }, Shadows.card]}
      onPress={() => handleNavigateToProfile(item.id)}
    >
      <View style={styles.cardContent}>
        {renderAvatar(item)}
        <View style={styles.userInfo}>
          <View style={styles.nameRow}>
            <ThemedText type="h4" numberOfLines={1} style={styles.userName}>
              {item.name}
            </ThemedText>
            {item.roles.includes('worker') ? renderLevelBadge(item.level, 'worker') : null}
            {item.verification?.status === 'approved' ? (
              <Feather name="check-circle" size={16} color={colors.success} />
            ) : null}
          </View>

          {item.location ? (
            <View style={styles.locationRow}>
              <Feather name="map-pin" size={12} color={colors.textSecondary} />
              <ThemedText type="small" style={{ color: colors.textSecondary }} numberOfLines={1}>
                {item.location}
              </ThemedText>
            </View>
          ) : null}

          {item.roles.includes('worker') ? (
            renderRating(item.averageRating, item.totalReviews)
          ) : null}

          <View style={styles.roleRow}>
            {item.roles.includes('producer') ? (
              <View style={[styles.roleTag, { backgroundColor: colors.primary + '20' }]}>
                <ThemedText type="small" style={{ color: colors.primary }}>
                  Produtor
                </ThemedText>
              </View>
            ) : null}
            {item.roles.includes('worker') ? (
              <View style={[styles.roleTag, { backgroundColor: colors.secondary + '20' }]}>
                <ThemedText type="small" style={{ color: colors.secondary }}>
                  Trabalhador
                </ThemedText>
              </View>
            ) : null}
          </View>
        </View>
      </View>

      <View style={styles.cardActions}>
        {renderActionButton(item)}
      </View>
    </Pressable>
  );

  const renderEmptyState = () => (
    <View style={styles.emptyState}>
      <View style={[styles.emptyIcon, { backgroundColor: colors.handshake + '20' }]}>
        <Feather name="users" size={48} color={colors.handshake} />
      </View>
      <ThemedText type="h3" style={styles.emptyTitle}>
        Nenhum usuario encontrado
      </ThemedText>
      <ThemedText type="body" style={[styles.emptyMessage, { color: colors.textSecondary }]}>
        Tente buscar por outro nome ou ajuste os filtros.
      </ThemedText>
    </View>
  );

  if (loading) {
    return (
      <ThemedView style={[styles.container, { paddingTop }]}>
        <View style={styles.loadingContainer}>
          <ActivityIndicator size="large" color={colors.handshake} />
          <ThemedText type="body" style={{ color: colors.textSecondary, marginTop: Spacing.md }}>
            Carregando usuarios...
          </ThemedText>
        </View>
      </ThemedView>
    );
  }

  return (
    <ThemedView style={[styles.container, { paddingTop }]}>
      <View style={[styles.searchContainer, { backgroundColor: colors.backgroundSecondary }]}>
        <Feather name="search" size={20} color={colors.textSecondary} />
        <TextInput
          style={[styles.searchInput, { color: colors.text }]}
          placeholder="Buscar por nome..."
          placeholderTextColor={colors.textSecondary}
          value={searchQuery}
          onChangeText={setSearchQuery}
          autoCapitalize="none"
          autoCorrect={false}
        />
        {searchQuery.length > 0 ? (
          <Pressable onPress={() => setSearchQuery('')} hitSlop={8}>
            <Feather name="x" size={20} color={colors.textSecondary} />
          </Pressable>
        ) : null}
      </View>

      <ScrollView
        horizontal
        showsHorizontalScrollIndicator={false}
        contentContainerStyle={styles.filtersContainer}
      >
        {FILTERS.map((filter) => (
          <Pressable
            key={filter.key}
            style={[
              styles.filterChip,
              activeFilter === filter.key
                ? { backgroundColor: colors.handshake }
                : { backgroundColor: colors.backgroundSecondary },
            ]}
            onPress={() => setActiveFilter(filter.key)}
          >
            <ThemedText
              type="small"
              style={[
                styles.filterText,
                { color: activeFilter === filter.key ? '#FFFFFF' : colors.text },
              ]}
            >
              {filter.label}
            </ThemedText>
          </Pressable>
        ))}
      </ScrollView>

      <FlatList
        data={filteredUsers}
        renderItem={renderUserCard}
        keyExtractor={(item) => item.id}
        contentContainerStyle={[
          styles.listContent,
          { paddingBottom: paddingBottom + Spacing.xl },
        ]}
        refreshControl={
          <RefreshControl
            refreshing={refreshing}
            onRefresh={handleRefresh}
            tintColor={colors.handshake}
          />
        }
        ListEmptyComponent={renderEmptyState}
        showsVerticalScrollIndicator={false}
        ItemSeparatorComponent={() => <View style={{ height: Spacing.md }} />}
      />
    </ThemedView>
  );
}

