              disabled={!newTalhaoName.trim() || isAddingTalhao}
            >
              {isAddingTalhao ? (
                <ActivityIndicator size="small" color="#FFFFFF" />
              ) : (
                <ThemedText type="body" style={{ color: '#FFFFFF' }}>Adicionar</ThemedText>
              )}
